import React, { useState, useEffect } from "react";
import classes from "./Nav.module.css";

const Nav = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);

  useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.getElementById("about");
      const aboutOffsetTop = aboutSection.offsetTop;
      const aboutOffsetBottom = aboutOffsetTop + aboutSection.offsetHeight;
      const navItems = document.querySelectorAll(`.${classes.nav} li`);

      navItems.forEach((item) => {
        const itemHref = item.querySelector("a").getAttribute("href");
        const targetSection = document.querySelector(itemHref);

        if (targetSection) {
          const targetOffsetTop = targetSection.offsetTop;
          const targetOffsetBottom =
            targetOffsetTop + targetSection.offsetHeight;

          if (
            window.pageYOffset >= targetOffsetTop &&
            window.pageYOffset < targetOffsetBottom &&
            itemHref !== `#${activeItem}`
          ) {
            setActiveItem(targetSection.id);
          } else if (
            window.pageYOffset >= aboutOffsetTop &&
            window.pageYOffset < aboutOffsetBottom &&
            itemHref !== "#about"
          ) {
            setActiveItem("about");
          }
        }
      });
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [activeItem]);

  const handleItemClick = (id) => {
    setActiveItem(id);
  };

  return (
    <nav className={classes.nav}>
      <ul className={classes["nav__list"]}>
        {props.navItems.map((item) => (
          <li
            className={`${classes["nav__item"]} ${
              activeItem === item.id ? classes["nav__item--current"] : ""
            }`}
            key={item.id}
            onClick={() => handleItemClick(item.id)}
          >
            <a href={`#${item.label.toLowerCase()}`}>{item.label}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Nav;
