import React, { useState, useEffect } from "react";
import classes from "./Nav.module.css";

const Nav = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);

  useEffect(() => {
    const handleScroll = () => {
      const sections = Array.from(document.getElementsByTagName("section"));
      const navItems = document.querySelectorAll(`.${classes.nav} li`);
      const scrollTop = window.pageYOffset;

      sections.forEach((section) => {
        const sectionTop = section.offsetTop - 60;
        const sectionBottom = sectionTop + section.offsetHeight;

        if (scrollTop >= sectionTop && scrollTop < sectionBottom) {
          setActiveItem(section.id);
        }
      });

      navItems.forEach((item) => {
        const itemHref = item.querySelector("a").getAttribute("href");

        if (itemHref === `#${activeItem}`) {
          item.classList.add(classes["nav__item--current"]);
        } else {
          item.classList.remove(classes["nav__item--current"]);
        }
      });
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [activeItem]);

  const handleItemClick = (id) => {
    setActiveItem(id);
  };

  return (
    <nav className={classes.nav}>
      <ul className={classes["nav__list"]}>
        {props.navItems.map((item) => (
          <li
            className={`${classes["nav__item"]} ${
              activeItem === item.id ? classes["nav__item--current"] : ""
            }`}
            key={item.id}
            onClick={() => handleItemClick(item.id)}
          >
            <a href={`#${item.label.toLowerCase()}`}>{item.label}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Nav;
