import React, { useState, useEffect } from "react";
import classes from "./Nav.module.css";

const Nav = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);

  useEffect(() => {
    const handleScroll = () => {
      props.navItems.forEach((navItem) => {
        const section = document.getElementById(navItem.id);
        if (section && section.getBoundingClientRect().top <= 0) {
          setActiveItem(navItem.id);
        }
      });
    };

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, [props.navItems]);

  const handleItemClick = (id) => {
    setActiveItem(id);
    const element = document.getElementById(id);
    if (element) {
      const offsetTop = element.offsetTop;
      window.scrollTo({
        top: offsetTop,
        behavior: "smooth",
      });
    }
  };

  return (
    <nav className={classes.nav}>
      <ul className={classes["nav__list"]}>
        {props.navItems.map((item) => (
          <li
            className={`${classes["nav__item"]} ${
              activeItem === item.id ? classes["nav__item--current"] : ""
            }`}
            key={item.id}
            onClick={() => handleItemClick(item.id)}
          >
            <a href={`#${item.label.toLowerCase()}`}>{item.label}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Nav;
