import React, { useEffect, useState, useRef } from "react";
import Home from "../../Section/Home/Home";
import About from "../../Section/About/About";
import Section from "../../Section/Section";
import Experience from "../../Section/Experience/Experience";

import classes from "./Main.module.css";

const Main = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);
  const sectionRefs = useRef([]);
  const handleScroll = () => {
    const scrollPosition = window.scrollY + 100;
    for (let i = sectionRefs.current.length - 1; i >= 0; i--) {
      const sectionTop = sectionRefs.current[i].offsetTop;
      if (scrollPosition >= sectionTop) {
        setActiveItem(props.navItems[i].id);
        break;
      }
    }
  };
  useEffect(() => {
    sectionRefs.current = sectionRefs.current.slice(0, props.navItems.length);
  }, [props.navItems]);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <main className={classes.main}>
      <div className={classes["main__inner"]}>
        {props.navItems.map((navItem, index) => (
          <Section
            className={classes.section}
            id={navItem.id}
            key={navItem.id}
            ref={(el) => (sectionRefs.current[index] = el)}
          >
            {navItem.id === "home" && <Home />}
            {navItem.id === "about" && <About />}
            {navItem.id === "experiences" && <Experience />}
          </Section>
        ))}
      </div>
    </main>
  );
};

export default Main;
