import React, { useState, useEffect } from "react";
import classes from "./Nav.module.css";

const Nav = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.pageYOffset;
      props.navItems.forEach((item) => {
        const section = document.querySelector(`#${item.id}`);
        console.log({ section });
        if (
          section.offsetTop - 100 <= currentScrollPos &&
          section.offsetTop + section.offsetHeight - 100 > currentScrollPos
        ) {
          setActiveItem(item.id);
        }
      });
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [props.navItems]);

  const handleItemClick = (id) => {
    setActiveItem(id);
  };

  return (
    <nav className={classes.nav}>
      <ul className={classes["nav__list"]}>
        {props.navItems.map((item) => (
          <li
            className={`${classes["nav__item"]} ${
              activeItem === item.id ? classes["nav__item--current"] : ""
            }`}
            key={item.id}
            onClick={() => handleItemClick(item.id)}
          >
            <a href={`#${item.id}`}>{item.label}</a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Nav;
