import React, { useEffect, useState, useRef } from "react";
import Home from "../../Section/Home/Home";
import About from "../../Section/About/About";
import Section from "../../Section/Section";
import Experience from "../../Section/Experience/Experience";

import classes from "./Main.module.css";

const Main = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);
  const sectionRefs = useRef([]);

  // Add event listener to window object to handle scroll event
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;

      // Find the section that is currently in view
      for (let i = 0; i < sectionRefs.current.length; i++) {
        const sectionTop = sectionRefs.current[i].offsetTop;
        const sectionBottom = sectionTop + sectionRefs.current[i].offsetHeight;

        if (scrollPosition >= sectionTop && scrollPosition < sectionBottom) {
          setActiveItem(props.navItems[i].id);
          break;
        }
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, [props.navItems]);

  return (
    <main className={classes.main}>
      <div className={classes["main__inner"]}>
        {props.navItems.map((navItem, index) => (
          <Section
            className={classes.section}
            id={navItem.id}
            key={navItem.id}
            ref={(el) => (sectionRefs.current[index] = el)} // Save a reference to the section element
          >
            {navItem.id === "home" && <Home />}
            {navItem.id === "about" && <About />}
            {navItem.id === "experiences" && <Experience />}
          </Section>
        ))}
      </div>
    </main>
  );
};

export default Main;
