import React, { useState } from "react";
import Card from "../../Card/Card";
import Modal from "../../Modal/Modal";
import Button from "../../UI/Button";

import classes from "./Experience.module.css";

const experiences = [
  {
    id: "1",
    title: "Back-End Developer",
    imageUrl:
      "https://www.whipmobility.com/wp-content/uploads/2022/08/WHIP-website-2-1024x724.jpg",
    duration: {
      startDate: "2021",
      endDate: "Present",
    },
    company: "Whip Mobility sdn",
    location: "Selangor, Malaysia",
    description:
      "WHIP  is a SaaS platform focused on delivering digital success for our automotive retail clients.",
    descriptions: [
      "WHIP  is a SaaS platform focused on delivering digital success for our automotive retail clients which empowers auto retailers to interact, sell and service their customers via a comprehensive digital engagement platform (inclusive of private-label app and web) that significantly improves the end-user experience and lowers costs (Shopify for automotive retailers!).",
    ],
    skills: [
      "NodeJs",
      "NestJs",
      "MongoDB",
      "Mongoose",
      "Typegoose",
      "JavaScript",
      "TypeScript",
      "Amazon Web Services",
      "Firestore",
      "Firebase",
      "REST API",
      "JSON",
      "Agile Methodologies",
      "Axios",
      "Git",
      "Postman",
    ],
  },
  {
    id: "2",
    title: "Front End Developer",
    imageUrl: "https://shahrenovin.com/z_themes/979/img/slider_1.png",
    duration: {
      startDate: "2018",
      endDate: "2021",
    },
    company: "ShahreNovin",
    location: "Tehran, Iran",
    description:
      "ShahreNovin Software Company, Provides all-in-one business management software that helps businesses manage everything can be kept organized in one place.",
    descriptions: [
      "The activity of ShahreNovin is providing Software As A Service (SAAS) and other cloud services in Iran and some other countries. We design and develop web-based software and services (cloud services) for the use of small and medium-sized (SME) businesses in Iran and any other Farsi language countries such as Uzbekistan, Tajikistan, Afghanistan, and other immigrant communities",
      "Through years of experience in the field of providing various software services to thousands of users from all over the country, we have succeeded in designing and developing the most comprehensive business management infrastructure on the scale of small and medium businesses.",
    ],
    skills: [
      "ReactJs",
      "Jacascript",
      "TypeScript",
      "jQuery",
      "Typescript",
      "Html",
      "Css",
      "Redux",
      "Hooks",
      "SCSS",
      "Styled-Component",
      "Bootstrap",
      "PWA",
    ],
  },
  {
    id: "3",
    title: "Front End Developer",
    imageUrl: "",
    duration: {
      startDate: "2015",
      endDate: "2018",
    },
    company: "Whip2",
    location: "Envento Market",
    description:
      "Website development is the process of building, programming, coding and maintaining websites and web applications.",
    descriptions: [
      "Elisc is a leading web design agency with an award-winning design team that creates innovative, effective websites that capture your brand, improve your conversion rates, and maximize your revenue to help grow your business and achieve your goals.",
      "In today’s digital world, your website is the first interaction consumers have with your business. That's why almost 95 percent of a user’s first impression relates to web design. It’s also why web design services can have an immense impact on your company’s bottom line.",
      "That’s why more companies are not only reevaluating their website’s design but also partnering with Elisc, the web design agency that’s driven more than $2.4 billion in revenue for its clients. With over 50 web design awards under our belt, we're confident we can design a custom website that drives sales for your unique business.",
    ],
    skills: [
      "NodeJs",
      "NestJs",
      "MongoDB",
      "Mongoose",
      "Typegoose",
      "JavaScript",
      "TypeScript",
      "Amazon Web Services",
      "Firestore",
      "Firebase",
      "REST API",
      "JSON",
      "Agile Methodologies",
      "Axios",
      "Git",
      "Postman",
    ],
  },
  {
    id: "4",
    title: "Web Developer",
    imageUrl: "",
    duration: {
      startDate: "2018",
      endDate: "2020",
    },
    company: "Whip3",
    location: "Envento Market",
    description:
      "Website development is the process of building, programming, coding and maintaining websites and web applications.",
    descriptions: [
      "Elisc is a leading web design agency with an award-winning design team that creates innovative, effective websites that capture your brand, improve your conversion rates, and maximize your revenue to help grow your business and achieve your goals.",
      "In today’s digital world, your website is the first interaction consumers have with your business. That's why almost 95 percent of a user’s first impression relates to web design. It’s also why web design services can have an immense impact on your company’s bottom line.",
      "That’s why more companies are not only reevaluating their website’s design but also partnering with Elisc, the web design agency that’s driven more than $2.4 billion in revenue for its clients. With over 50 web design awards under our belt, we're confident we can design a custom website that drives sales for your unique business.",
    ],
    skills: [
      "NodeJs",
      "NestJs",
      "MongoDB",
      "Mongoose",
      "Typegoose",
      "JavaScript",
      "TypeScript",
      "Amazon Web Services",
      "Firestore",
      "Firebase",
      "REST API",
      "JSON",
      "Agile Methodologies",
      "Axios",
      "Git",
      "Postman",
    ],
  },
];

const Experience = (props) => {
  const [modalIsShown, setModalIsShown] = useState(false);
  const [selectedExperience, setSelectedExperience] = useState(null);

  const showModalHandler = () => {
    setModalIsShown(true);
  };

  const hideModalHandler = () => {
    setModalIsShown(false);
  };

  const handleExperienceClick = (experience) => {
    setSelectedExperience(experience);
  };

  return (
    <div className={classes.experience} id={props.id}>
      <div className={classes.content}>
        <div className={classes.experience__title}>
          <span>- Experience</span>
          <h3>Places I worked so far!</h3>
        </div>
        <div className={classes.experience__list}>
          <ul>
            {experiences.map((experience) => (
              <li key={experience.id}>
                <Card
                  className={classes.hover}
                  onClick={() => {
                    showModalHandler();
                    handleExperienceClick(experience);
                  }}
                >
                  <div className={classes.job}>
                    <div className={classes.job__info}>
                      <span>{`-${experience.duration.startDate} -${experience.duration.endDate}`}</span>
                      <h3>{`${experience.title}`}</h3>
                    </div>
                    <div className={classes.job__place}>
                      <span>{`-${experience.location}`}</span>
                    </div>
                  </div>
                  <div className={classes.description}>
                    <p>{experience.description}</p>
                    <Button className={classes["button--customised"]}>
                      Read More
                    </Button>
                  </div>
                </Card>
              </li>
            ))}
          </ul>
          {modalIsShown && (
            <Modal onModalBackdropClick={hideModalHandler}>
              <div className={classes.modal__image}>
                <div
                  style={{
                    backgroundImage: `url(${selectedExperience.imageUrl})`,
                  }}
                ></div>
              </div>
              <div className={classes.modal__tags}>
                {selectedExperience.skills.map((skill) => (
                  <span>{skill}</span>
                ))}
              </div>
              <div className={classes.modal__info}>
                <span>{selectedExperience.company}</span>
                <span>{`-${selectedExperience.duration.startDate} -${selectedExperience.duration.endDate}`}</span>
                <h3>{selectedExperience.title}</h3>
              </div>
              <div className={classes.modal__description}>
                {selectedExperience.descriptions.map((description) => (
                  <p key={selectedExperience.id}>{description}</p>
                ))}
              </div>
            </Modal>
          )}
        </div>
      </div>
    </div>
  );
};

export default Experience;
