import React, { useState } from "react";

import classes from "./Nav.module.css";

const Nav = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen);
  };

  const navClass = props.isMobile
    ? `${classes["nav-mobile"]}`
    : classes["nav-aside"];

  // const navClass = !isOpen ? `${classes.nav} ${classes.mobile}` : classes.nav;

  return (
    <>
      <nav className={navClass}>
        <ul className={classes["nav__list"]}>
          <li
            className={`${classes["nav__item"]} ${classes["nav__item--current"]}`}
          >
            <a href="#home">Home</a>
          </li>
          <li className={classes["nav__item"]}>
            <a href="#about">About</a>
          </li>
          <li className={classes["nav__item"]}>
            <a href="#projects">Projects</a>
          </li>
          <li className={classes["nav__item"]}>
            <a href="#contact">Contact</a>
          </li>
        </ul>
      </nav>
      {props.isMobile && (
        <header className={classes.header}>
          <span>Mahdi Zangeneh</span>
          <div className={classes.container} onClick={handleClick}>
            <div
              className={`${classes.bar} ${isOpen ? classes.change : ""}`}
            ></div>
            <div
              className={`${classes.bar} ${isOpen ? classes.change : ""}`}
            ></div>
            <div
              className={`${classes.bar} ${isOpen ? classes.change : ""}`}
            ></div>
          </div>
        </header>
      )}
    </>
  );
};

export default Nav;
