import React, { useState, useEffect } from "react";

import classes from "./Aside.module.css";
import Author from "./Author";
import Copyright from "./Copyright";
import Nav from "./Nav";

const Aside = () => {
  const [isMobile, setIsMobile] = useState(false);
  const [showNav, setShowNav] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 1040);
    };

    window.addEventListener("resize", handleResize);

    handleResize();

    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const navClasses = `${classes.nav} ${showNav ? classes.showNav : ""}`;

  const final = !isMobile ? (
    <aside className={classes.sidebar}>
      <div className={classes["sidebar__inner"]}>
        <Author />
        <Nav />
        <Copyright />
      </div>
    </aside>
  ) : (
    <>
      <button className={classes.menuButton} onClick={() => setShowNav(true)}>
        Menu
      </button>
      <Nav className={navClasses} />
    </>
  );

  return final;
};

export default Aside;
