import React, { useEffect, useState, useRef } from "react";
import Home from "../../Section/Home/Home";
import About from "../../Section/About/About";
import Section from "../../Section/Section";
import Experience from "../../Section/Experience/Experience";

import classes from "./Main.module.css";
import Nav from "../Nav/Nav";

const Main = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);
  const sectionRefs = useRef(
    props.navItems.reduce((acc, value) => {
      acc[value.id] = React.createRef();
      return acc;
    }, {})
  );

  useEffect(() => {
    const handleScroll = () => {
      const sections = Object.values(sectionRefs.current);
      const currentSectionIndex = sections.findIndex((section) => {
        const { top, bottom } = section.current.getBoundingClientRect();
        return top <= 0 && bottom > 0;
      });

      if (currentSectionIndex >= 0) {
        setActiveItem(props.navItems[currentSectionIndex].id);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, [props.navItems]);

  return (
    <main className={classes.main}>
      <div className={classes["main__inner"]}>
        {props.navItems.map((navItem) => (
          <Section
            className={classes.section}
            id={navItem.id}
            key={navItem.id}
            ref={sectionRefs.current[navItem.id]}
          >
            {navItem.id === "home" && <Home />}
            {navItem.id === "about" && <About />}
            {navItem.id === "experiences" && <Experience />}
          </Section>
        ))}
      </div>
      <Nav navItems={props.navItems} activeItem={activeItem} />
    </main>
  );
};

export default Main;
