import React, { useState, useEffect } from "react";
import Home from "../../Section/Home/Home";
import About from "../../Section/About/About";
import Section from "../../Section/Section";
import Experience from "../../Section/Experience/Experience";
import classes from "./Main.module.css";
import Nav from "../Nav/Nav";

const Main = (props) => {
  const [activeItem, setActiveItem] = useState(props.navItems[0].id);

  useEffect(() => {
    const handleScroll = () => {
      const sectionPositions = {};
      props.navItems.forEach((navItem) => {
        const section = document.getElementById(navItem.id);
        if (section) {
          sectionPositions[navItem.id] = {
            top: section.getBoundingClientRect().top + window.scrollY,
            bottom: section.getBoundingClientRect().bottom + window.scrollY,
          };
        }
      });
      const currentSection = Object.keys(sectionPositions).find((sectionId) => {
        const sectionTop = sectionPositions[sectionId].top;
        const sectionBottom = sectionPositions[sectionId].bottom;
        return window.scrollY >= sectionTop && window.scrollY < sectionBottom;
      });
      if (currentSection) {
        setActiveItem(currentSection);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [props.navItems]);

  return (
    <div>
      <Nav
        navItems={props.navItems}
        activeItem={activeItem}
        handleItemClick={setActiveItem}
      />
      <main className={classes.main}>
        <div className={classes["main__inner"]}>
          {props.navItems.map((navItem) => (
            <Section
              className={classes.section}
              id={navItem.id}
              key={navItem.id}
            >
              {navItem.id === "home" && <Home />}
              {navItem.id === "about" && <About />}
              {navItem.id === "experiences" && <Experience />}
            </Section>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Main;
